import React, { useState } from "react";
import { useCart } from "../context/CartContext";
import "./Cart.css";

function Cart() {
  const { state, dispatch } = useCart();
  const [address, setAddress] = useState("");
  const [landmark, setLandmark] = useState("");

  const totalPrice = state.cartItems.reduce(
    (acc, item) => acc + item.price * item.quantity,
    0
  );

  const handleProceedToPay = () => {
    if (!address.trim()) {
      alert("Please enter a delivery address.");
    } else {
      alert("Jumping to payment method or payment gateway...");
    }
  };

  return (
    <div className="cart-floating-container">
      <div className="cart-page">
        <h2>Your Cart</h2>
        {state.cartItems.length === 0 ? (
          <p>Your cart is empty.</p>
        ) : (
          <>
            <div className="cart-items-list">
              {state.cartItems.map((item) => (
                <div className="cart-item" key={item.id}>
                  <img
                    src={item.image}
                    alt={item.name}
                    className="cart-image"
                  />
                 <div className="cart-info">
  <div className="cart-main">
    <h4>{item.name}</h4>
    <p><strong>Price:</strong> â‚¹{item.price}</p>
    <p><strong>Quantity:</strong> {item.quantity}</p>
    <button
      onClick={() =>
        dispatch({
          type: "REMOVE_FROM_CART",
          payload: item.id,
        })
      }
    >
      Remove
    </button>
  </div>
  <div className="cart-description">
    {item.description || "This product is perfect for your daily needs and combines both style and functionality."}
  </div>
</div>

                </div>
              ))}
            </div>

            <div className="cart-summary">
              <h3>Total: â‚¹{totalPrice}</h3>

              <div className="address-section">
                <label htmlFor="address">Delivery Address:</label>
                <textarea
                  id="address"
                  rows="3"
                  placeholder="Enter your delivery address..."
                  value={address}
                  onChange={(e) => setAddress(e.target.value)}
                ></textarea>

                <label htmlFor="landmark">Nearby Landmark:</label>
                <input
                  id="landmark"
                  type="text"
                  placeholder="e.g., Near City Mall or XYZ Hospital"
                  value={landmark}
                  onChange={(e) => setLandmark(e.target.value)}
                />
              </div>

              <button className="proceed-btn" onClick={handleProceedToPay}>
                Proceed to Pay
              </button>
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default Cart;
