import React from "react";
import { useCart } from "../context/CartContext";
import { Link } from "react-router-dom";

const Cart = () => {
  const { cartItems = [], removeFromCart, getTotalPrice } = useCart(); // provide default empty array

  const isCartEmpty = !Array.isArray(cartItems) || cartItems.length === 0;

  return (
    <div className="min-h-screen bg-gray-100 py-10 px-4 md:px-10">
      <h2 className="text-3xl font-semibold mb-6 text-center text-gray-800">Your Shopping Cart</h2>

      {isCartEmpty ? (
        <div className="bg-white p-6 rounded shadow-md text-center">
          <p className="text-gray-600 text-lg mb-4">Your cart is currently empty.</p>
          <Link to="/" className="text-white bg-blue-600 hover:bg-blue-700 px-5 py-2 rounded transition">
            Shop Now
          </Link>
        </div>
      ) : (
        <div className="space-y-6">
          {cartItems.map((item) => (
            <div
              key={item.id}
              className="flex flex-col md:flex-row items-center justify-between bg-white p-4 rounded-lg shadow-md"
            >
              <div className="flex items-center space-x-4 w-full md:w-auto">
                <img src={item.image} alt={item.name} className="w-24 h-24 object-contain rounded" />
                <div>
                  <h3 className="text-xl font-semibold text-gray-800">{item.name}</h3>
                  <p className="text-gray-500">₹{item.price}</p>
                  <p className="text-sm text-gray-400">Qty: {item.quantity}</p>
                </div>
              </div>
              <button
                onClick={() => removeFromCart(item.id)}
                className="mt-4 md:mt-0 text-red-600 hover:text-red-800 font-medium"
              >
                Remove
              </button>
            </div>
          ))}

          <div className="bg-white p-6 rounded-lg shadow-md text-right">
            <h3 className="text-xl font-bold text-gray-700">Total: ₹{getTotalPrice()}</h3>
            <Link
              to="/checkout"
              className="inline-block mt-4 bg-green-600 hover:bg-green-700 text-white px-6 py-2 rounded-lg transition"
            >
              Proceed to Checkout
            </Link>
          </div>
        </div>
      )}
    </div>
  );
};

export default Cart;
